var floor = 0;
var positionCount = 0;
foreach (var character in input)
{
    positionCount++;
    if (character == '(')
    {
        floor++;
    }
    else if (character == ')')
    {
        floor--;
    }
    if (floor == -1)
    {
        break;
    }
}
